mapper.insert:
INSERT INTO jung_member
	(
		idx
		, username
		, password
		, email
		, name
		, nickName
		, phone
		, stAddress
		, dtAddress
		, birthDate
		, gender
		, role
	)
VALUES
	(
		jung_member_idx_seq.nextval
		, #{username}
		, #{password}
		, #{email}
		, #{name}
		, #{nickName}
		, #{phone}
		, #{stAddress}
		, #{dtAddress}
		, #{birthDate}
		, #{gender}
		, #{role}
	)

mapper.update:
UPDATE jung_member SET

		password = #{password}


		nickName = #{nickName}			


		stAddress = #{stAddress},
		dtAddress = #{dtAddress}


		,phone = #{phone}			

WHERE
	idx = #{idx}

mapper.select:
select R.* from ( select rownum rnum, Q.* from ( select * from jung_member
username like '%' || #{search} || '%' or name like '%' || #{search} || '%' or nickName like '%' || #{search} || '%'
order by idx desc ) Q where
rownum <= #{endNo}
) R where
rnum >= #{startNo}

mapper.update:
UPDATE jung_member SET
	role = #{role}
WHERE
	idx = #{idx}
